INSERT INTO media VALUES ('ltr_gwa2300','letter','1778-10-28','NY','state','US','George','Washington','','Aaron','Burr','','You, in my opinion, carry your ideas of delicacy too far when you propose to drop your pay while the recovery of your health necessarily requires your absence from the service.','ltr_gwa2300.txt','hans@broughdesign.com',NULL,Now(),NULL,'Project Gutenberg\'s Memoirs of Aaron Burr, Volume 1., by Matthew L. Davis, 1836');
INSERT INTO media VALUES ('ltr_gwa2301','letter','1779-04-03','Middlebrook','NJ','US','George','Washington','','Aaron','Burr','','Perfectly satisfied that no consideration save a desire to reestablish your health could induce you to leave the service, I cannot therefore withhold my consent.','ltr_gwa2301.txt','hans@broughdesign.com',NULL,Now(),NULL,'Project Gutenberg\'s Memoirs of Aaron Burr, Volume 1., by Matthew L. Davis, 1836');
INSERT INTO media VALUES ('ltr_gwa2302','letter','1779-05-19','Middlebrook','NJ','US','George','Washington','','','','','It is much to be regretted that the pleasure of obeying the first emotions in favour of misfortune is not always in our power.','ltr_gwa2302.txt','hans@broughdesign.com',NULL,Now(),NULL,'Project Gutenberg\'s Memoirs of Aaron Burr, Volume 1., by Matthew L. Davis, 1836');
