INSERT INTO media VALUES ('ltr_peg2300','letter','1800-12-03','New York','NY','US','Peggy','Gartin','','Aaron','Burr','','I have asked James to write to you to know how the venison was to be done; but I will now have it cured as you have ordered.','ltr_peg2300.txt','hans@broughdesign.com',NULL,Now(),NULL,'Project Gutenberg\'s Memoirs of Aaron Burr, Volume 1., by Matthew L. Davis, 1836');
INSERT INTO media VALUES ('ltr_peg2301','letter','1800-12-17','New York','NY','US','Peggy','Gartin','','Aaron','Burr','','I have found there is a day-school, kept by an elderly man and his wife, near to our house, and if master is willing that I should go to it for two months, I think it would be of great service to me','ltr_peg2301.txt','hans@broughdesign.com',NULL,Now(),NULL,'Project Gutenberg\'s Memoirs of Aaron Burr, Volume 1., by Matthew L. Davis, 1836');
