INSERT INTO media VALUES ('ltr_jde2400','letter','1806-10-13','Baltimore','MD','US','J','Deveruex','','Harman','Blennerhassett','','individual','','How grateful and flattered do I feel, my valued sir, for the good opinion you are pleased to express and entertain of me. On this subject I shall only further add, that I trust I shall never disappoint you','ltr_jde2400.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Blennerhassett Papers, Embodying the Private Journal of Barman Blennerhassitt, and the Hitherto Unpublished Correspondence..., 1864 ');
INSERT INTO media VALUES ('ltr_jde2401','letter','1808-09-09','London','','ENG','J','Deveruex','','Harman','Blennerhassett','','individual','','What trials, what misfortunes, have you not undergone, and your dear and worthy Mrs. Blennerhassett too !','ltr_jde2401.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Blennerhassett Papers, Embodying the Private Journal of Barman Blennerhassitt, and the Hitherto Unpublished Correspondence..., 1864 ');
