INSERT INTO media VALUES ('ltr_edr4550','letter','1794-01-10','Philadelphia','PA','US','Edmund','Randolph','','Gouverneur','Morris','','individual','','I do myself the honor of enclosing to you a letter from Mr Jefferson, announcing my succession to him, as Secretary of State.','ltr_edr4550.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Life of Gouverneur Morris With Selections from His Correspondence and Miscellaneous Papers Vol. II., Jared Sparks, 1832');
INSERT INTO media VALUES ('ltr_edr4551','letter','1794-04-21','Philadelphia','PA','US','Edmund','Randolph','','','Fouchet','','individual','','I beg you to accept this measure, as a fresh proof of our sincere desire to maintain friendship with your nation ; and to assure you, at the same time, that as no delay occurred','ltr_edr4551.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Life of Gouverneur Morris With Selections from His Correspondence and Miscellaneous Papers Vol. II., Jared Sparks, 1832');
INSERT INTO media VALUES ('ltr_edr4552','letter','1794-04-29','Philadelphia','PA','US','Edmund','Randolph','','Gouverneur','Morris','','The severity practised upon us by Great Britain, in her instructions of the 8th of June and 6th of November, 1793, has caused various movements in Congress.','','description','ltr_edr4552.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Life of Gouverneur Morris With Selections from His Correspondence and Miscellaneous Papers Vol. II., Jared Sparks, 1832');
