INSERT INTO media VALUES ('ltr_edp4000','letter','1779-10-11','Edmundsbury','','ENG','Edmund','Pendleton','','John','Jay','','individual','','I congratulate you, sir, upon your appointment to represent the American States at the court of Madrid ; the just testimony of that confidence which the honourable body you have presided over, have in your abilities and integrity.','ltr_edp4000.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Life John Jay With Selections from His Correspondence and Miscellaneous Papers. by His Son, William Jay in Two Volumes. Vol. II., 1833.');
