INSERT INTO media VALUES ('ltr_ewr4000','letter','1776-11-24','Philadelphia','PA','US','Edward','Rutledge','','John','Jay','','individual','','I expected long ere this to have been seated quietly at home ; but the progress which the enemy had made, and seemed likely to make, into your country, induced me to suspend my resolution','ltr_ewr4000.txt','hans@broughdesign.com',NULL,Now(),NULL,'The Life John Jay With Selections from His Correspondence and Miscellaneous Papers. by His Son, William Jay in Two Volumes. Vol. II., 1833.');
