INSERT INTO media VALUES ('ltr_arc3700','letter','1780-10-07','West Point','NY','US','Arthur','St. Clair','','George','Washington','','individual','','The laying in a proper stock of fire-wood is a very heavy, but a very essential business, and the militia, which have been employed in cutting it, I am told, have done little more than supply the post at Fishkill','ltr_arc3700.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume III., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_arc3701','letter','1781-01-07','Morristown','NJ','US','Arthur','St. Clair','','George','Washington','','individual','','Your Excellency has heard of the shameful defection of the Pennsylvania line; and I am very much concerned to inform you that, as yet, there is no prospect, that we know of, of any desire appearing in them to return to their duty.','ltr_arc3701.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume III., Jared Sparks, 1853');
