INSERT INTO media VALUES ('ltr_bnh4300','letter','1784-06-10','','NC','US','Benjamin','Hawkins','','George','Washington','','individual','','I have the honor to inclose to your Excellency some acts passed at the last session of our Legislature, by which you will see, in some measure, the disposition of this State to comply with the views of Congress','ltr_bnh4300.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_bnh4301','letter','1792-02-10','','NC','US','Benjamin','Hawkins','','George','Washington','','individual','','Prompted by the free and candid manner you expressed yourself in political affairs to me some days past, I shall, without reserve, communicate to you the reasons which induced me yesterday to vote for striking out the second section in the bill','ltr_bnh4301.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
