INSERT INTO media VALUES ('ltr_gec4300','letter','1783-04-17','Poughkeepsie','NY','US','George','Clinton','','George','Washington','','individual','','It appears to me indispensably necessary that some troops should be kept in service in time of peace, for the purpose of garrisoning the posts, which it may be thought expedient to maintain on the frontiers, and to protect the public magazines.','ltr_gec4300.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_gec4301','letter','1783-10-14','Poughkeepsie','NY','US','George','Clinton','','George','Washington','','individual','','We have as yet no certainty when the British will leave the southern district of this State, though all accounts agree that their stay will not exceed the 10th of next month.','ltr_gec4301.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
