INSERT INTO media VALUES ('ltr_joj3200','letter','1779-04-21','Philadelphia','PA','US','John','Jay','','George','Washington','','individual','','I was sure of your politeness, but not certain of a more than usual degree of confidence. The latter has now been made manifest, and, permit me to assure you, it shall be mutual.','ltr_joj3200.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume II., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_joj3201','letter','1779-04-26','Philadelphia','PA','US','John','Jay','','George','Washington','','individual','','While the maritime affairs of the Continent continue under the direction of a Committee, they will be exposed to all the consequences of want of system, attention, and knowledge.','ltr_joj3201.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume II., Jared Sparks, 1853');
