INSERT INTO media VALUES ('ltr_tom4300','letter','1789-02-12','Fayette County','KY','US','Thomas','Marshall','','George','Washington','','individual','','It appears plain to me that the offers of Lord DorChester, as well as those of Spain, are founded on a supposition that it is a fact that we are about to separate from the Union ; else, why are those offers not made to Congress?','ltr_tom4300.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_tom4301','letter','1789-06-26','Woodford County','KY','US','Thomas','Marshall','','George','Washington','','individual','','Permit me. Sir, to express my extreme happiness on account of your Excellency\'s acceptance of the Presidency of the United States','ltr_tom4301.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume IV., Jared Sparks, 1853');
