INSERT INTO media VALUES ('ltr_thm3000','letter','1776-08-06','Mount Washington','NH','US','Thomas','Mifflin','','George','Washington','','individual','','Agreeably to your order, by Colonel Reed\'s letter, I have directed Colonel Holden to march with his three companies this evening to King\'s Bridge.','ltr_thm3000.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume I., Jared Sparks, 1853');
INSERT INTO media VALUES ('ltr_thm3001','letter','1776-12-28','Bristol','PA','US','Thomas','Mifflin','','George','Washington','','individual','','I came here at four o clock this afternoon. Five hundred men, sent from Philadelphia yesterday, crossed to Burlington this morning. This evening I sent over near three hundred more.','ltr_thm3001.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume I., Jared Sparks, 1853');
