INSERT INTO media VALUES ('ltr_thw3200','letter','1777-10-24','Lancaster','PA','US','Thomas','Wharton','','George','Washington','','individual','','In consequence of this application we have ordered out two classes of militia, offering an exemption from their fines to such delinquents of the former classes as can be prevailed upon to join them','ltr_thw3200.txt','hans@broughdesign.com',NULL,Now(),NULL,'Correspondence of the American Revolution; Being Letters of Eminent Men to George Washington, from the Time of His Taking Command of the Army to the End of His Presidency, Volume II., Jared Sparks, 1853');
