INSERT INTO media VALUES ('ltr_swk8020','letter','1847-01-16','Los Angeles','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I am informed that you are now engaged in organizing a civil government and appointing officers for it in this territory.','ltr_swk8020.txt','hans@broughdesign.com',NULL,Now(),NULL,'A sketch of the life of Com. Robert F. Stockton, 1856, Derby ',0);
INSERT INTO media VALUES ('ltr_swk8021','letter','1847-01-17','Los Angeles','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I must, for the purpose of preventing a collision between us and possibly a civil war in consequence of it, remain silent for the present...','ltr_swk8021.txt','hans@broughdesign.com',NULL,Now(),NULL,'A sketch of the life of Com. Robert F. Stockton, 1856, Derby ',0);
INSERT INTO media VALUES ('ltr_swk8022','letter','1847-01-17','Los Angeles','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I have to inform you that I intend to withdraw to-morrow from this place, with the small party which escorted me to this country. ','ltr_swk8022.txt','hans@broughdesign.com',NULL,Now(),NULL,'A sketch of the life of Com. Robert F. Stockton, 1856, Derby ',0);
INSERT INTO media VALUES ('ltr_swk8023','letter','1847-01-13','Los Angeles','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I further fear that, unless something is done at once to inform them of the true state of affairs here, they may capitulate and retire to the upper country. ','ltr_swk8023.txt','hans@broughdesign.com',NULL,Now(),NULL,'A sketch of the life of Com. Robert F. Stockton, 1856, Derby ',0);
INSERT INTO media VALUES ('ltr_swk8024','letter','1847-01-12','Los Angeles','CA','US','Stephen','Kearny','','John','Fremont','','individual','','I am here in possession of this place, with sailors and marines.','ltr_swk8024.txt','hans@broughdesign.com',NULL,Now(),NULL,'A sketch of the life of Com. Robert F. Stockton, 1856, Derby ',0);
