INSERT INTO media VALUES ('ltr_swk8000','letter','1846-12-02','Camp At Warners','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I (this afternoon) reached here, escorted by a party of the First Regiment Dragoons. I came by orders from the President of the United States.','ltr_swk8000.txt','hans.brough@yahoo.com',NULL,Now(),NULL,'Memoirs of My Life, by John Charles Fremont. 1887, Chicago, New York, Belford, Clarke & Company',0);
INSERT INTO media VALUES ('ltr_swk8001','letter','1846-12-22','San Diego','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','I do not think that Lieutenant-Colonel Fremont should be left unsupported to fight a battle upon which the fate of California may, for a long time, depend...','ltr_swk8001.txt','hans.brough@yahoo.com',NULL,Now(),NULL,'Memoirs of My Life, by John Charles Fremont. 1887, Chicago, New York, Belford, Clarke & Company',0);
INSERT INTO media VALUES ('ltr_swk8002','letter','1846-12-23','San Diego','CA','US','Stephen','Kearny','','Robert','Stockton','','individual','','You certainly could not for a moment suppose that I would advise or suggest to you any movement which might endanger the safety of the garrison and the ships in the harbor. ','ltr_swk8002.txt','hans.brough@yahoo.com',NULL,Now(),NULL,'Memoirs of My Life, by John Charles Fremont. 1887, Chicago, New York, Belford, Clarke & Company',0);
