INSERT INTO media VALUES ("ltr_alg5848","letter","1809-11-08","Washington","MD","US","Albert","Gallatin","","Thomas","Jefferson","","individual","","I perused your affectionate letter of the 11th ult. with lively sensations of pleasure, excited by that additional evidence of your continued kindness and partiality.","ltr_alg5848.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,42,0);
