INSERT INTO media VALUES ("ltr_alg5858","letter","1812-00-00","","","US","Albert","Gallatin","","James","Madison","","individual","","I never have from personal considerations interfered with appointments, but for once feel compelled to do it.","ltr_alg5858.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,42,0);
