INSERT INTO media VALUES ("ltr_alg5860","letter","1812-03-10","Washington","MD","US","Albert","Gallatin","","Thomas","Jefferson","","individual","","You have seen from your retreat that our hopes and endeavors to preserve peace during the present European contest have at last been frustrated.","ltr_alg5860.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,42,0);
