INSERT INTO media VALUES ("ltr_alg5862","letter","1812-06-10","Treasury Department","MD","US","Albert","Gallatin","","Langdon","Cheves","","individual","","I had the honor to receive your letter of yesterday, asking whether, in my opinion, the Non-Importation Act may not be so modified, or partially suspended, as to afford a revenue equivalent to the estimated amount of the internal taxes","ltr_alg5862.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,42,0);
