INSERT INTO media VALUES ("ltr_dol1358","letter","0000-00-00","","","US","Dolly","Madison",NULL,"","Mason",NULL,"individual",NULL,"I am obliged, my charming friend, to announce to you the disappointment in my hopes of attending your agreeable party to Alexandria...","ltr_dol1358.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1359","letter","1804-04-26","","","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"Dolly Madison letter to sister Anna.","ltr_dol1359.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1361","letter","1804-06-00","Washington","DC","US","Dolly","Madison",NULL,"","",NULL,"individual",NULL,"Dolly Madison letter to sister Anna","ltr_dol1361.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1362","letter","1804-07-16","Washington","DC","US","Dolly","Madison",NULL,"","",NULL,"individual",NULL,"Dolly Madison letter to sister Anna.","ltr_dol1362.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1363","letter","1804-04-00","","","US","Dolly","Madison",NULL,"Mrs. Isaac","Winston",NULL,"individual",NULL,"Dolly Madison letter to Mrs. Issac.","ltr_dol1363.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1364","letter","1804-00-00","","","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"Where will you celebrate the Fourth of July, my dear sister ? We are to have grand doings here.","ltr_dol1364.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1365","letter","1804-05-22","","","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"There is so much I could tell you about these new French people...","ltr_dol1365.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1366","letter","1804-06-03","Montpelier","VA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"I have been very ill since I wrote last, with inflammatory rheumatism - never had I more extreme pain in sickness.","ltr_dol1366.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1367","letter","1805-06-04","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"I write to you from my bed, to which I have been confined for ten days with a bad knee...","ltr_dol1367.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1368","letter","1805-07-08","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"My knee will keep me in WASHINGTON longer, I fear, than will be conducive to our health or interest.","ltr_dol1368.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1369","letter","1805-07-29","Philadelphia","PA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"My health and spirits revived every day with the drive...","ltr_dol1369.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1370","letter","1805-07-31","Philadelphia","PA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"Dr. Physic has put my knee in splints and promises me a cure in time.","ltr_dol1370.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1371","letter","1805-10-23","Philadelphia","PA","US","Dolly","Madison",NULL,"James","Madison",NULL,"individual",NULL,"A few hours only have passed since you left me, my beloved, and I find nothing can relieve the oppression of my mind but speaking to you...","ltr_dol1371.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,1);
INSERT INTO media VALUES ("ltr_dol1372","letter","1805-10-26","Philadelphia","PA","US","Dolly","Madison",NULL,"James","Madison",NULL,"individual",NULL,"In my dreams of last night, I saw you in your chamber, unable to move, from riding so far and so fast.","ltr_dol1372.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1373","letter","1805-10-30","Philadelphia","PA","US","Dolly","Madison",NULL,"James","Madison",NULL,"individual",NULL,"To find you love me, have my child safe, and that my mother is well, seems to comprise all my happiness.","ltr_dol1373.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1374","letter","1805-11-01","Philadelphia","PA","US","Dolly","Madison",NULL,"James","Madison",NULL,"individual",NULL,"the doctor has just told me that I may reasonably hope to leave this place in a fortnight...","ltr_dol1374.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1375","letter","1805-11-15","Philadelphia","PA","US","Dolly","Madison",NULL,"James","Madison",NULL,"individual",NULL,"I see that Jackson's paper has announced the declaration of war from Spain against us...","ltr_dol1375.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1376","letter","1805-08-04","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Payne",NULL,"individual",NULL,"Dolly and Lucy both gone! they are now angels...","ltr_dol1376.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1377","letter","1807-05-27","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"The President has a sick headache every day, and is obliged to retire to a dark room every morning by nine o clock.","ltr_dol1377.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1378","letter","1808-08-28","Montpelier","VA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"With heartfelt joy, my beloved sister, did I receive the short letter of my brother, giving the good tidings of your third son...","ltr_dol1378.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1380","letter","1811-12-20","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"No Constitution heard of yet - the Hornet went to take despatches and to let them know our determination to fight for our rights.","ltr_dol1380.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1382","letter","1812-03-20","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"The Federalists affronted to a man.","ltr_dol1382.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1383","letter","1812-00-00","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"I wrote you that the Embargo would take place three or four days before it did","ltr_dol1383.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1384","letter","1812-05-12","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"John Randolph has been firing away at the 'House' this morning against the declaration of war, but we think it will have little effect.","ltr_dol1384.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1385","letter","1811-11-15","Washington","DC","US","Dolly","Madison",NULL,"Joel","Barlow",NULL,"individual",NULL,"Your notes giving us an account of your progress on the water were grateful, indeed, but the news of your safe arrival in France infinitely more so.","ltr_dol1385.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1386","letter","1811-00-00","Washington","DC","US","Dolly","Madison",NULL,"Joel","Barlow",NULL,"individual",NULL,"Before this, you know of our Embargo, to be followed by War!! Yes, that terrible event is at hand...","ltr_dol1386.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1387","letter","1813-05-12","Washington","DC","US","Dolly","Madison",NULL,"Edward","Coles",NULL,"individual",NULL,"For the last week all the city and Georgetown (except the Cabinet) have expected a visit from the enemy...","ltr_dol1387.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1388","letter","1813-06-10","Washington","DC","US","Dolly","Madison",NULL,"Edward","Coles",NULL,"individual",NULL,"About the picture, if Mr. D. will return it safe in six months, more or less, he may send for it as soon as ever he chooses.","ltr_dol1388.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1389","letter","1813-07-02","","","US","Dolly","Madison",NULL,"Edward","Coles",NULL,"individual",NULL," Mr. Madison recovers - for the last three weeks his fever has been so slight as to permit him to take bark every hour and with good effect.","ltr_dol1389.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1390","letter","1819-09-05","Montpelier","DC","US","Dolly","Madison",NULL,"Edward","Coles",NULL,"individual",NULL,"I am afraid, dear cousin, that while you and I deliberate who to choose for a wife, we shall lose some -of the finest girls now grown.","ltr_dol1390.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1392","letter","1814-08-23","Washington","DC","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"he left, beseeching me to take care of myself, and of the Cabinet papers, public and private.","ltr_dol1392.txt","hans.brough@yahoo.com",NULL,20110717124935,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1401","letter","1823-04-00","Montpelier","VA","US","Dolly","Madison",NULL,"Payne","Todd",NULL,"individual",NULL,"I am impatient to hear from you, my dearest Payne","ltr_dol1401.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1402","letter","1824-12-02","Montpelier","VA","US","Dolly","Madison",NULL,"Payne","Todd",NULL,"individual",NULL,"Dolly Madison letter to son Payne Todd.","ltr_dol1402.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_pbm1403","letter","1824-01-19","Washington","DC","US","Phoebe","Morris",NULL,"Dolly","Madison",NULL,"individual",NULL,"PHOEBE MORRIS letter to Dolly Madison...We all attended Mrs. Adams's reception on the 8th, and it was really a very brilliant party.","ltr_pbm1403.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1404","letter","1826-00-00","Montpelier","VA","US","Dolly","Madison",NULL,"Sarah","Stevenson",NULL,"individual",NULL,"Dolly Madison letter to cousin Sarah Coles Stevenson... that my husband is fixed here, and hates to have me leave him...","ltr_dol1404.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1405","letter","1826-07-26","Montpelier","VA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"Yesterday we had ninety persons to dine with us at one table, put up on the lawn, under a thick arbor.","ltr_dol1405.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1406","letter","1826-07-30","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"Dolly Madison letter to her neice Mary Cutts....We had old Mr. Patterson and his son Edward from Baltimore to stay with us several days, and they tell me that Madame Bonaparte is still in France","ltr_dol1406.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1407","letter","1830-03-10","Montpelier","VA","US","Dolly","Madison",NULL,"Dolly","Cutts",NULL,"individual",NULL,"Imagine, if you can, a greater trial to the patience of us farmers than the destruction of a radiant patch of green peas by frost!","ltr_dol1407.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1408","letter","1830-11-30","Montpelier","VA","US","Dolly","Madison",NULL,"Dolly","Cutts",NULL,"individual",NULL,"I find you have no idea yet of the improvement love can make...","ltr_dol1408.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1409","letter","1831-01-05","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"you must help her in every way you can, keep her room quiet, and herself free from the slightest agitation or uneasiness.","ltr_dol1409.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1410","letter","1831-09-18","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"Your Uncle Madison still wears the bead ring you placed on his finger, and I see him look at it every now and then without saying anything.","ltr_dol1410.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1411","letter","1831-12-00","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"I have been the most disconsolate of persons these three or four days, and all because of a violent toothache.","ltr_dol1411.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1412","letter","1832-08-12","Montpelier","VA","US","Dolly","Madison",NULL,"Anna","Cutts",NULL,"individual",NULL,"Mrs. Mason has just written to me to say you are a little better...","ltr_dol1412.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1413","letter","1832-08-05","Montpelier","VA","US","Dolly","Madison",NULL,"","",NULL,"individual",NULL,"The heart of your miserable sister mourns with you and for your dear children.","ltr_dol1413.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1414","letter","1833-08-01","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"There is one secret, however, she did not tell you, and that is the power we all have in forming our own destinies.","ltr_dol1414.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1415","letter","1833-09-09","Montpelier","VA","US","Dolly","Madison",NULL,"Dolly","Cutts",NULL,"individual",NULL,"I was as glad to get a letter from you yesterday as I was to receive a present of a pair of the celebrated English Holkim calves, sent me by Mr. Patterson...","ltr_dol1415.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1416","letter","1834-12-02","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"Our sex are ever losers, when they stem the torrent of public opinion.","ltr_dol1416.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1417","letter","1834-07-20","Montpelier","VA","US","Dolly","Madison",NULL,"Payne","Todd",NULL,"individual",NULL,"Dolly Madison letter to son Payne Todd...Mr. Madison is better, though very ill a few days since, and I now hope he will soon be well enough for me to leave him on an expedition...","ltr_dol1417.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1418","letter","1835-05-11","Montpelier","VA","US","Dolly","Madison",NULL,"Dolly","Cutts",NULL,"individual",NULL,"My days are devoted to nursing and comforting my sick patient, who walks only from the bed in which he break fasts to another, in the little room in which you left him...","ltr_dol1418.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1419","letter","1835-10-31","Montpelier","VA","US","Dolly","Madison",NULL,"Mary","Cutts",NULL,"individual",NULL,"Have you any amusing books, no matter how old, to lend me?","ltr_dol1419.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1420","letter","1836-07-10","Montpelier","VA","US","Dolly","Madison",NULL,"Andrew","Jackson",NULL,"individual",NULL,"Dolly Madison letter to President Jackson...I am now preparing to execute the trust his confidence reposed in me, that of placing before Congress and the world what his pen had prepared for their use...","ltr_dol1420.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol1421","letter","1836-07-05","Montpelier","VA","US","Dolly","Madison",NULL,"Richard","Cutts",NULL,"individual",NULL,"Dolly Madison letter to brother Richard Cutts...I could never doubt your sympathy, dear brother, and require it much now. When can you come and see me?","ltr_dol1421.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,57,0);
INSERT INTO media VALUES ("ltr_dol5950","letter","1836-11-08","Montpelier","VA","US","Dolley","Madison","","Henry","Clay","","individual","","The sources of consolation in my bereavement which you suggest, are those which my heart can most truly appreciate.","ltr_dol5950.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,47,0);
