INSERT INTO media VALUES ("ltr_eks1588","letter","1845-08-28","Cincinnati","OH","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"the news from Texas is such, that there can be but little doubt that we must be ordered almost immediately to that field.","ltr_eks1588.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1589","letter","1845-09-09","New Orleans","LA","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We left CINCINNATI on the twenty-ninth ultimo, having been strengthened by fifty recruits from the depot at Newport.","ltr_eks1589.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1590","letter","1845-08-18","Corpus Christi","TX","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"At night, the fourteenth, during a violent thunder storm, we cast anchor opposite to the camp fires of the Army of Occupation.","ltr_eks1590.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1591","letter","1845-11-02","Corpus Christi","TX","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We hear nothing here about our movements, but are lying under our canvas on the shelly beach of this calm sea in a perfect state of monotony.","ltr_eks1591.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1593","letter","1846-03-17","TAMANLIPAS","TX","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We, the Second Brigade, left Corpus Christi at eight in the morning on the tenth.","ltr_eks1593.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1594","letter","1846-03-29","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"On the twenty-sixth we advanced our camp three miles where we awaited General Taylors return.","ltr_eks1594.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1595","letter","1846-04-09","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We have been as busy as a light infantry company on drill ever since we arrived eleven days ago.","ltr_eks1595.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1596","letter","1846-04-28","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"Since my last letter we have had busy and exciting times.","ltr_eks1596.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1597","letter","1846-05-03","Point Isabel","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"Day before yesterday we left our fort in command of Major Brown...","ltr_eks1597.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1598","letter","1846-05-07","Point Isabel","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"the enemy had fired near fifteen hundred balls and shells at the work","ltr_eks1598.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1599","letter","1846-05-10","Battle field near Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We pushed on and by two o clock deployed in order of battle, the Mexicans showing themselves in line in much superior numbers.","ltr_eks1599.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1600","letter","1846-05-13","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"On the eighth, as I anticipated, we met the enemy on the prairie and fought them five hours whipping them severely.","ltr_eks1600.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1601","letter","1846-05-19","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"That I was not killed is wonderful as I was in the ravine where so many fell...","ltr_eks1601.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1602","letter","1846-05-30","Matamoras","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"One of the most violent storms which has assailed us in this region is now raging and it has cost me ... much labor to secure my tent.","ltr_eks1602.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1603","letter","1846-09-05","Steamboat North Carolina","MO","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"There is no end to human calamity!","ltr_eks1603.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1604","letter","1846-09-06","Cairo","MO","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"You perhaps remember this place at the mouth of the Ohio.","ltr_eks1604.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1605","letter","1846-09-23","New Orleans","LA","US","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"I have a sad tale to tell you of all my ill luck since I wrote you from CAIRO.","ltr_eks1605.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1606","letter","1846-09-30","Steamer off Brasos","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"It is early morning. We have lain here rolling in a heavy sea since yesterday.","ltr_eks1606.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1607","letter","1846-10-12","Camargo","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"This is the hottest and dustiest place I have ever seen...","ltr_eks1607.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1608","letter","1846-10-26","Monterey","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife -","ltr_eks1608.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1609","letter","1846-11-02","Monterey","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"The scenery far surpasses anything in the States, and our traveled friends say, anything in Europe.","ltr_eks1609.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1610","letter","1846-11-08","Monterey","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1610.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1611","letter","1846-11-23","Saltillo","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1611.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1612","letter","1846-12-16","Saltillo","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1612.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1613","letter","1847-01-22","Steamboat - Rough and Ready","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1613.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1614","letter","1847-01-27","Camp Palo Alto","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1614.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1615","letter","1847-02-17","CAMP AT BRASOS","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife","ltr_eks1615.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1616","letter","1847-02-28","SHIP HURON OFF LOBOS","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"It is rumored that we are to commence our landing tonight I doubt it.","ltr_eks1616.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1617","letter","1847-03-13","Camp near Vera Cruz","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We could see a few Mexican soldiers on shore, but no evidence of any large force to oppose our landing...","ltr_eks1617.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1618","letter","1847-03-22","Camp near Vera Cruz","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"The siege has progressed slowly and neither party has as yet injured the other materially.","ltr_eks1618.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1619","letter","1847-03-24","Camp near Vera Cruz","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"I have just come in from a picket tour of three days and two nights","ltr_eks1619.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1620","letter","1847-04-06","Camp near Vera Cruz","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"E. Kirby Smith letter to wife -","ltr_eks1620.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1621","letter","1847-04-17","Camp near Vera Cruz","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"we were ordered on board the steamer McKim to go up the Alvarado River after horses and mules.","ltr_eks1621.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1622","letter","1847-04-25","Jalapa","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We were then nearly worn out with fatigue and started in bad spirits","ltr_eks1622.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1623","letter","1847-04-29","CASTLE OF PEROTE","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"The gorgeous flowers of this region are now in full bloom","ltr_eks1623.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1624","letter","1847-04-30","CASTLE OF PEROTE","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"What fools the Mexicans are that they don't make peace!","ltr_eks1624.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1625","letter","1847-05-03","HACIENDO, SAN ANTONIO","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"the volunteers have fled in every action in which they have been engaged...","ltr_eks1625.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1626","letter","1847-05-10","VEREYES","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"we are on the verge of a peace","ltr_eks1626.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1627","letter","1847-05-15","PUEBLA","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"The papers here state that Santa Anna lost ninety men in the skirmish yesterday.","ltr_eks1627.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1628","letter","1847-05-27","PUEBLA","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"We hear from Mexico this evening that Santa Anna has resigned all his offices","ltr_eks1628.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1629","letter","1847-06-04","PUEBLA","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"My birthday I am forty years old","ltr_eks1629.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
INSERT INTO media VALUES ("ltr_eks1630","letter","1847-08-22","TACUBAYA","","MEX","Ephraim","Smith",NULL,"","Smith",NULL,"individual",NULL,"My brain is whirling from the long continued excitement and my body sore with bruises and fatigue...","ltr_eks1630.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,48,0);
