INSERT INTO media VALUES ("ltr_jqa2082","letter","1795-08-15","hague","","NED","John Quincy","Adams",NULL,"Sylvanus","Bourne",NULL,"individual",NULL,"The object of the consular office is the benefit of merchants and mariners in countries other than their own - it is to procure facilities and conveniences for them that this institution is appropriated.","ltr_jqa2082.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,33,0);
INSERT INTO media VALUES ("ltr_jqa2083","letter","1795-08-20","hague","","NED","John Quincy","Adams",NULL,"Edmund","Randolph",NULL,"individual",NULL,"In many parts of Europe at this time a passport is a paper of indispensable necessity to every traveller, and indeed to every individual in a country other than his own.","ltr_jqa2083.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,33,0);
