INSERT INTO media VALUES ("ltr_jqa2087","letter","1795-09-12","Hague","","NED","John Quincy","Adams",NULL,"John","Adams",NULL,"individual",NULL,"The conduct of the British government is so well adapted to increasing our danger of war, that I cannot but suppose they are secretly inclined to produce it.","ltr_jqa2087.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,33,0);
