INSERT INTO media VALUES ("ltr_jqa2094","letter","1795-11-17","London","","ENG","John Quincy","Adams",NULL,"John","Adams",NULL,"individual",NULL,"An English paper that I saw at Rotterdam on the day of my departure from the Hague gave me the first information of Mr. Randolph's resignation.","ltr_jqa2094.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,33,0);
