INSERT INTO media VALUES ("ltr_jqa2104","letter","1796-03-20","London","","ENG","John Quincy","Adams",NULL,"John","Adams",NULL,"individual",NULL,"The people indeed everywhere ardently sigh for peace. Everywhere they perceive that they have been made the victims of their own passions and follies.","ltr_jqa2104.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,33,0);
