INSERT INTO media VALUES ("ltr_lew7020","letter","1861-03-01","Montgomery","AL","US","Leroy","Walker","","Francis","Pickens","","individual","","In controlling the military operations in the harbor of Charleston, the President directs me to say that everything will be done that may be due to the honor and rights of South Carolina.","ltr_lew7020.txt","hans@broughdesign.com",NULL,20090901034854,NULL,50,0);
