INSERT INTO media VALUES ("ltr_jbm8000","letter","1846-06-03","San Francisco","CA","US","John","Montgomery","","John","Fremont","","individual","","Permit me to say, sir, that if you should find it convenient to visit the U. S. Ship Portsmouth during her stay in this port, that I, with the officers of the ship, will be most happy to see you.","ltr_jbm8000.txt","hans.brough@yahoo.com",NULL,20120424102149,NULL,100,0);
