INSERT INTO media VALUES ("ltr_jbm8002","letter","1846-06-16","Sausalito","CA","US","John","Montgomery","","William","Ide","","individual","","Permit me, sir, in response to your call for powder for the use of your party, to say that I am here as a representative of a government at peace (as far as I know) with Mexico and her province of California...","ltr_jbm8002.txt","hans@broughdesign.com",NULL,20120427110348,NULL,100,0);
