INSERT INTO media VALUES ("ltr_mtl1735","letter","1867-10-06","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Mary Todd Lincoln letter to Elizabeth Keckly...I am writing this morning with, a broken heart after a sleepless night of great mental suffering.","ltr_mtl1735.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,1);
INSERT INTO media VALUES ("ltr_mtl1736","letter","1867-10-08","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Bowed down with suffering and anguish, again I write you.","ltr_mtl1736.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1737","letter","1867-10-09","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"It appears as if the fiends had let loose, for the Republican papers are tearing me to pieces...","ltr_mtl1737.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1738","letter","1867-10-13","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Was there ever such cruel newspaper abuse lavished upon an unoffending woman as has been showered upon my devoted head?","ltr_mtl1738.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1739","letter","1867-10-29","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"I received a very pleasant note from Mr. F. Douglass on yesterday.","ltr_mtl1739.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1740","letter","1867-11-02","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Their conduct is becoming mysterious. We have heard enough of their talk it is time now they should be acting.","ltr_mtl1740.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1741","letter","1867-11-09","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Do write me every other day at least, I am so nervous and miserable.","ltr_mtl1741.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1742","letter","1867-11-09","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Do not hint to K. or B., or any one else, my doubts of them, only watch them.","ltr_mtl1742.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1743","letter","1867-11-15","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"I should have been able to live as the widow of the great President Lincoln should, with sufficient means to give liberally to all","ltr_mtl1743.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1744","letter","1867-11-17","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"By the time you receive this note, you will doubtless find the papers raving over the large income which we are each said to have.","ltr_mtl1744.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1745","letter","1867-11-17","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Show the note enclosed with this to B. & K. - do not let them retain it an instant after reading","ltr_mtl1745.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1746","letter","1867-11-21","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"These circulars must bring some money.","ltr_mtl1746.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1747","letter","1867-11-23","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"between ourselves, I fear I am in villanous hands.","ltr_mtl1747.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1748","letter","1867-11-24","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"And the laugh of the world will be against me if it turns out as I now think","ltr_mtl1748.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1749","letter","1867-12-26","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"Mary Todd Lincoln letter to Elizabeth Keckly","ltr_mtl1749.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1750","letter","1867-12-27","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"I am positively suffering for a decent dress.","ltr_mtl1750.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1751","letter","1868-01-12","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"I am so miserable I feel like taking my own life.","ltr_mtl1751.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_ezk1752","letter","1868-01-01","Chicago","IL","US","Elizabeth","Keckly",NULL,"BISHOP","PAYNE",NULL,"individual",NULL,"Allow me to donate certain valuable relics, to be exhibited for the benefit of Wilberforce University","ltr_ezk1752.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1753","letter","1868-01-15","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"The loss I deserve for being so careless, but it comes very hard on poor me.","ltr_mtl1753.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1754","letter","1868-02-07","Chicago","IL","US","Mary","Todd",NULL,"","Brady",NULL,"individual",NULL,"I hereby authorize Mrs. Keckley to request my bill from you","ltr_mtl1754.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1755","letter","1867-09-18","Chicago","IL","US","Mary","Todd",NULL,"","Brady",NULL,"individual",NULL,"I have this day sent to you personal property, which I am compelled to part with, and which you will find of considerable value.","ltr_mtl1755.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1756","letter","1867-00-00","Chicago","IL","US","Mary","Todd",NULL,"","Brady",NULL,"individual",NULL,"The articles I am sending you to dispose of were gifts of dear friends, which only urgent necessity compels me to part with...","ltr_mtl1756.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1757","letter","1867-09-25","Chicago","IL","US","Mary","Todd",NULL,"","Brady",NULL,"individual",NULL,"I must have means to live, at least in a medium comfortable state.","ltr_mtl1757.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,0);
INSERT INTO media VALUES ("ltr_mtl1758","letter","1867-10-06","Chicago","IL","US","Mary","Todd",NULL,"Elizabeth","Keckly",NULL,"individual",NULL,"My ink is like myself and my spirits failing, so I write you to-day with a pencil.","ltr_mtl1758.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,91,1);
