INSERT INTO media VALUES ("ltr_pah1916","letter","1777-06-05","Williamsburg","VA","US","Patrick","Henry",NULL,"","",NULL,"individual",NULL,"The signal honour conferred on me by the General Assembly in their choice of me to be Governor of this commonwealth, demands my best acknowledgments...","ltr_pah1916.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,105,0);
