INSERT INTO media VALUES ("ltr_pah3200","letter","1777-10-30","Williamsburg","VA","US","Patrick","Henry","","George","Washington","","individual","","Although it seems impossible to enlist Continental recruits here, yet the zeal of our countrymen is great and general in the public cause. I wish some proper exertions of that spirit were wisely directed to the general good.","ltr_pah3200.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,73,0);
INSERT INTO media VALUES ("ltr_pah3201","letter","1777-12-06","Williamsburg","VA","US","Patrick","Henry","","George","Washington","","individual","","Inclosed I have the honor to transmit you an ac count of necessaries sent off in nine wagons, for the Virginia regiments in Continental service. The goods have been delivered to Colonel Finnic, the Quarter master","ltr_pah3201.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,73,0);
INSERT INTO media VALUES ("ltr_pah3202","letter","1779-03-13","Williamsburg","VA","US","Patrick","Henry","","George","Washington","","individual","","My last accounts from the South are unfavorable. Georgia is said to be in full possession of the enemy, and South Carolina in great danger. The number of disaffected there is said to be formidable, and the Creek Indians inclining against us.","ltr_pah3202.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,73,0);
