INSERT INTO media VALUES ("ltr_pbp5960","letter","1828-07-15","Washington","MD","US","Peter Buell","Porter","","Henry","Clay","","individual","","For the first ten days of my official labor, or, rather, reconnoissance, I found myself located in a field so entirely new and strange, that I could not move a single step without encountering some serious obstacle.","ltr_pbp5960.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,47,0);
