INSERT INTO media VALUES ("ltr_thj4550","letter","1793-01-13","Philadelphia","PA","US","Thomas","Jefferson","","Gouverneur","Morris","","individual","","It has long since been observed, that, of the three millions of livres given by the court of France to aid us in the commencement of our revolution, one million was unaccounted for by the hands into which it was paid.","ltr_thj4550.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,39,0);
