INSERT INTO media VALUES ("ltr_vmx6000","letter","1827-03-02","Baltimore","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","I feel a strong sympathy for Vande Venter, who, tho' strong in conscious integrity, must nevertheless, feel a deep mortification at the impression that his abrupt dismissal from office is calculated to make","ltr_vmx6000.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6001","letter","1829-04-06","Washington","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","I met with Green (Duff) yesterday and found him evidently in an ill humour with the State of things here.","ltr_vmx6001.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6002","letter","1829-04-09","Annapolis","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","The Report has got out and been carried to Baltimore, that I have been proposed for the office of Treasurer.","ltr_vmx6002.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6003","letter","1829-05-07","Annapolis","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","Gen'l. Green, who has had a complete blow up with Eaton, is under the same impression, and feels the same disgust. He considers Eaton as intriguing and entirely selfish.","ltr_vmx6003.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6004","letter","1829-06-01","Annapolis","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","He satisfied me from the perfectly frank and cordial manner in which he spoke of his great obligations to and regard for you","ltr_vmx6004.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6005","letter","1829-07-04","Tulip Hill","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","A very unpleasant state of things has arisen between him and General Green, which he was desirous you should be acquainted with, but about which he felt reluctant to write.","ltr_vmx6005.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
INSERT INTO media VALUES ("ltr_vmx6006","letter","1843-12-03","Tulip Hill","MD","US","Virgil","Maxcy","","John","Calhoun","","individual","","It would not be safe to go into any convention, however formed, with the Richmond and Albany politicians.","ltr_vmx6006.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,68,0);
