INSERT INTO media VALUES ("ltr_wts1112","letter","1860-05-08","Alexandria","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"Last night I got the copy of the speech and read it...","ltr_wts1112.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
