INSERT INTO media VALUES ("ltr_wts1114","letter","1861-01-16","Alexandria","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I am so much in the woods here that I can't keep up with the times at all.","ltr_wts1114.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1115","letter","1861-01-18","Alexandria","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I regard the seizure by Governor Moore of the United States Arsenal as the worst act yet committed in the present revolution.","ltr_wts1115.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1116","letter","1861-04-22","St. Louis","MO","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"At a moment like this the country expects every man to do his duty.","ltr_wts1116.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1117","letter","1861-04-25","St. Louis","MO","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"Virginia's secession influences some six millions of people.","ltr_wts1117.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1118","letter","1861-05-08","St. Louis","MO","US","William","Sherman",NULL,"S","Cameron",NULL,"individual",NULL,"I hold myself now, as always, prepared to serve my country in the capacity for which I was trained.","ltr_wts1118.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1119","letter","1861-05-11","St. Louis","MO","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I was a witness of the firing on the people by the United States Militia at Camp Jackson yesterday.","ltr_wts1119.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1120","letter","1861-05-20","St. Louis","MO","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"The greatest difficulty in the problem now before the country is not to conquer but so conquer as to impress upon the real men of the South a respect for their conquerors.","ltr_wts1120.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1121","letter","1861-05-22","St. Louis","MO","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I received your despatch last evening stating I would be appointed Colonel of one of the new 3 Battalion Regiments","ltr_wts1121.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
