INSERT INTO media VALUES ("ltr_wts1140","letter","1862-11-24","Memphis","TN","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I am just back from Columbus, Ky., where I went to meet Gen. Grant.","ltr_wts1140.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
