INSERT INTO media VALUES ("ltr_wts1147","letter","1863-02-18","Camp before Vicksburg","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"We have reproached the South for arbitrary conduct in coercing their people at last we find we must imitate their example.","ltr_wts1147.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1148","letter","1863-04-03","Camp before Vicksburg","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"I confess I am astonished at your industry and acquirements.","ltr_wts1148.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
