INSERT INTO media VALUES ("ltr_wts1150","letter","1863-04-23","Camp before Vicksburg","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"If not too late, do, for mercy's sake, exhaust your influence to stop this consolidation of regiments.","ltr_wts1150.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
INSERT INTO media VALUES ("ltr_wts1151","letter","1863-04-26","Camp before Vicksburg","LA","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"To-morrow I start with my corps to bring up the rear of the movement against Grand Gulf, and, maybe, Jackson, Miss.","ltr_wts1151.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
