INSERT INTO media VALUES ("ltr_wts1153","letter","0000-00-00","Jackson","MS","US","William","Sherman",NULL,"John","Sherman",NULL,"individual",NULL,"The share I have personally borne in all these events is one in which you may take pride for me.","ltr_wts1153.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,55,0);
