INSERT INTO media VALUES ("ltr_zat5950","letter","1847-12-28","Baton Rouge","LA","US","Zachary","Taylor","","Henry","Clay","","individual","","I left Mexico after it was determined the column under my orders was to act on the defensive, and after the capital of the enemy had fallen into our hands, and their army dispersed, on a short leave of absence, to visit my family","ltr_zat5950.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,47,0);
