INSERT INTO media VALUES ('ltr_frh6950','letter','1780-10-29','Camp Between Broad River And The Catawba','SC','US','Francis','Rawdon-Hastings','','Henry','Clinton','','individual','','Lord Cornwallis having been so reduced by a severe fever as to be still unable to write, he has desired that I should have the honour of addressing your Excellency in regard to our present situation.','ltr_frh6950.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_frh6951','letter','1780-10-31','Camp Between Broad River And The Catawba','SC','US','Francis','Rawdon-Hastings','','Alexander','Leslie','','individual','','I stated the events which had frustrated our hopes of penetrating into the heart of North Carolina, and arming the loyalists in our cause','ltr_frh6951.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_frh6952','letter','1781-04-25','Camden','SC','US','Francis','Rawdon-Hastings','','Charles','Cornwallis','','individual','','We therefore attacked Greene at 10 o\'clock this morning.','ltr_frh6952.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
