INSERT INTO media VALUES ('ltr_hnc6950','letter','1778-10-08','New York','NY','US','Henry','Clinton','','George','Germain','','individual','','On the return of the troops from the expedition to Bedford, &c., I proposed taking a forward position with the army, as well to procure a supply of forage','ltr_hnc6950.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6951','letter','1778-11-24','New York','NY','US','Henry','Clinton','','George','Germain','','individual','','I have consented that Lieut-General Earl Cornwallis should return to England','ltr_hnc6951.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6952','letter','1779-08-20','New York','NY','US','Henry','Clinton','','George','Germain','','individual','','I must beg leave to express how happy I am made by the return of Lord Cornwallis to this country.','ltr_hnc6952.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6953','letter','1780-07-14','Phillipsburgh','PA','US','Henry','Clinton','','Charles','Cornwallis','','individual','','I request you will send me as soon as possible what troops you can spare.','ltr_hnc6953.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
