INSERT INTO media VALUES ('ltr_hnc6955','letter','1780-11-10','New York','NY','US','Henry','Clinton','','George','Germain','','individual','','you will observe that he recommends a diversion in the Chesapeak as essential to favour his operations.','ltr_hnc6955.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6956','letter','1781-04-30','New York','NY','US','Henry','Clinton','','Charles','Cornwallis','','individual','','I make no doubt that you will think it right to communicate to Major-General Phillips without delay the plan of your future operations in that quarter','ltr_hnc6956.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6957','letter','1781-09-06','New York','NY','US','Henry','Clinton','','Charles','Cornwallis','','individual','','As I find by your letters that De Grasse has got into the Chesapeak, and I can have no doubt that Washington is moving with at least 6000 French and Rebel troops against you.','ltr_hnc6957.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
INSERT INTO media VALUES ('ltr_hnc6958','letter','1781-09-24','New York','NY','US','Henry','Clinton','','Charles','Cornwallis','','individual','','At a meeting of the General and Flag Officers, held this day, it is determined that about 5000 men, rank and file, shall be embarked aboard the King\'s ships','ltr_hnc6958.txt','hans.brough@yahoo.com',NULL,20090802183836,NULL,'Correspondence of Charles, first Marquis Cornwallis, Vol I, Charles Ross, Esq., London, 1859',0);
