CREATE TABLE person (
  person_id int(5) NOT NULL,
  person_DOB date default NULL,
  person_DOD date default NULL,
  person_first varchar(50) default NULL,
  person_middle varchar(50) default NULL,
  person_last varchar(50) default NULL,
  person_father varchar(50) default NULL,
  person_mother varchar(50) default NULL,
  person_tla varchar(5) default NULL,
  person_sex char(1) default NULL,
  short_description varchar(255) default NULL,
  description text,
  PRIMARY KEY  (person_id)
);


select person_DOB as DOB, person_DOD as DOD, person_first as firstName, person_middle as middleName, person_last as lastName, person_father as father, person_mother as mother, person_tla as tla, person_sex as gender,short_description as description from person;
