INSERT INTO media VALUES ("ltr_roh3900","letter","1776-10-06","charleston","SC","US","Robert","Howe","","John","Rutledge","","individual","","The command of the Military Department in this State devolving upon me, I feel it my duty to lay before your Excellency, and by your means before the Honorable Council and Assembly, my sentiments","ltr_roh3900.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,104,0);
INSERT INTO media VALUES ("ltr_roh3901","letter","1776-11-07","charleston","SC","US","Robert","Howe","","","","","position","President Of The Convention Of North Carolina","I have the honor to transmit you, by the Hon. Mr. Drayton, the copy of a resolution of the Council of this State, for obtaining the aid of 1,500 minute men, or militia men, from North Carolina","ltr_roh3901.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,104,0);
INSERT INTO media VALUES ("ltr_roh3907","letter","1778-01-04","Savannah","SC","US","Robert","Howe","","","Ladson","","individual","","You request in your letter that I would candidly give my opinion of your conduct, while you acted under me in a military capacity","ltr_roh3907.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,104,0);
