INSERT INTO media VALUES ("ltr_wlh6950","letter","1776-12-20","New York","NY","US","William","Howe","","George","Germain","","individual","","In Jersey, upon the approach of the van of Lord Cornwallis' corps to Brunswick by a forced march on the 1st inst, the enemy went off most precipitately to Princetown","ltr_wlh6950.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,"83",0);
INSERT INTO media VALUES ("ltr_wlh6951","letter","1777-01-05","New York","NY","US","William","Howe","","George","Germain","","individual","","In consequence of the advantage gained by the enemy at Trenton on the 26th of last month, and the necessity of an alteration in the cantonments, Lord Cornwallis, deferring his going to England by this opportunity","ltr_wlh6951.txt","hans.brough@yahoo.com",NULL,20090802183836,NULL,"83",0);
